INSERT INTO QUOTES (quotes_text, quotes_refepisode, quotes_refcharacter) VALUES ('MAIS ARRETEZ J''ARRETE PAS DE RETROUVER DES PTITES BOULETTES DE CACA PARTOUT ! Me prenez pas pour un con, sans blague, c''était une bergerie ici ?', 156, 34);
INSERT INTO QUOTES (quotes_text, quotes_refepisode, quotes_refcharacter) VALUES ('J''ai toujours dit que je supportais pas les jupes ; mais c''est l''uniforme réglementaire, j''y suis pour rien !', 155, 34);
INSERT INTO QUOTES (quotes_text, quotes_refepisode, quotes_refcharacter) VALUES ('Jamais je ne trahirai. Ave César ! Ave César ! […] La seule chose que je voudrais savoir, c''est si dans deux jours, je change d''avis… Est-ce que la proposition tient toujours ?', 157, 34);
INSERT INTO QUOTES (quotes_text, quotes_refepisode, quotes_refcharacter) VALUES ('Le poisson qui étouffe sur la berge remue plus que celui qui est dans l''eau. C''est bien la preuve de ce que je dis : si on se débat, c''est qu''on est en train de crever.', 158, 34);
INSERT INTO QUOTES (quotes_text, quotes_refepisode, quotes_refcharacter) VALUES ('Non, mais là vous auriez pu prévenir qu''il y avait un couvert de plus. Bon ça va que j''ai pas fait à manger, mais bon, c''est chaud.', 156, 34);
INSERT INTO QUOTES (quotes_text, quotes_refepisode, quotes_refcharacter) VALUES ('Ah bah en fait, c''est tout moisi seigneur breton. Parce qu''en plus, il faut que je nourrisse des mecs.', 156, 34);
