INSERT INTO QUOTES (quotes_text, quotes_refepisode, quotes_refcharacter) VALUES ('C''est vexant de me ressembler ? C''est pour ça qu''on vous a choisi et qu''on vous a sorti de votre cambrousse, alors du respect ! ', 161, 35);
INSERT INTO QUOTES (quotes_text, quotes_refepisode, quotes_refcharacter) VALUES ('Et surtout : pensez POUVOIR ! ', 162, 35);
INSERT INTO QUOTES (quotes_text, quotes_refepisode, quotes_refcharacter) VALUES ('Par exemple, je trouve que quand elle est partie du jour au lendemain sans prévenir, elle s''est conduite comme la reine des putes. ', 52, 35);
INSERT INTO QUOTES (quotes_text, quotes_refepisode, quotes_refcharacter) VALUES ('Si j''échoue, je garde la butte aux Cerfs et je fais tomber une pluie de calamités sur le royaume par vengeance ! C''est pas pour rien qu''on m''appelle «le Fourbe». Si je réussis, je garde la butte aux Cerfs, plus… Le tertre des Âmes…', 70, 36);
