INSERT INTO QUOTES (quotes_text, quotes_refepisode, quotes_refcharacter) VALUES ('(À Bohort qui vient de lui déclarer que Lancelot est amoureux d''elle)Mais qu''est-ce que vous déconnez, Seigneur Bohort ? Vous avez picolé ?', 25, 38);
INSERT INTO QUOTES (quotes_text, quotes_refepisode, quotes_refcharacter) VALUES ('(À Lancelot)On a beau expliquer tout ce qu''on veut expliquer… Au bout d''un moment, pour savoir faire du cheval, il faut faire du cheval !', 183, 38);
