INSERT INTO QUOTES (quotes_text, quotes_refepisode, quotes_refcharacter) VALUES ('Ah non, avec les jolies seulement. C''est de là que j''ai conclu que, comme y m''touchait pas, je faisais moi-même partie des grosses mochetés.', 184, 38);
INSERT INTO QUOTES (quotes_text, quotes_refepisode, quotes_refcharacter) VALUES ('Je voulais savoir pourquoi vous laissiez tous vos chevaliers former des clans séparatistes, alors que l''une de vos plus importantes missions a toujours été de fédérer les clans existants autour de votre royaume et de la quête du Graal ? ... Enfin c''était pas tout à fait ça...', 185, 38);
INSERT INTO QUOTES (quotes_text, quotes_refepisode, quotes_refcharacter) VALUES ('(À propos des moutons) Mais je n''comprends pas, c''est pas des moutons ça ? […] Bah ils sont tout... tout bizarres... […] Tondus ? Oh mon Dieu mais quelle horreur ! Mais pourquoi vous avez fait ça ?!', 129, 38);
INSERT INTO QUOTES (quotes_text, quotes_refepisode, quotes_refcharacter) VALUES ('Attention, attention… il va y arriver un moment où il y a des granges qui vont se mettre à flamber, faudra pas demander d''où ça vient !', 74, 39);
INSERT INTO QUOTES (quotes_text, quotes_refepisode, quotes_refcharacter) VALUES ('Je vais rentrer mes vaches, là, elles sont accessibles ou faut que je fasse monter un échafaudage ?', 91, 39);
INSERT INTO QUOTES (quotes_text, quotes_refepisode, quotes_refcharacter) VALUES ('Ce que je dis, tout le monde s''en tamponne ! Je gueule, je gueule, j''pourrais gueuler dans le cul d''un poney ce serait pareil !', 186, 39);
INSERT INTO QUOTES (quotes_text, quotes_refepisode, quotes_refcharacter) VALUES ('(À Roparzh) Non mais vous allez faire chier le monde pendant combien de temps vous encore avec vos vaches pourries là, hein ?! Vous voyez pas que vous êtes pas fait pour l''agriculture ?! Hein, vous avez qu''à vous recycler dans la mendicité ! En plus, vous avez… Vous avez déjà la tenue !', 187, 39);
INSERT INTO QUOTES (quotes_text, quotes_refepisode, quotes_refcharacter) VALUES ('Je lui rembourse mes genoux, et si il a filé la vérole à mes bêtes, chuis chuis un marteau moi ! Je crame tout moi ! Ma ferme, la sienne, celles des autres, le château, j''vais flamber la moitié de la Bretagne.', 187, 39);
INSERT INTO QUOTES (quotes_text, quotes_refepisode, quotes_refcharacter) VALUES ('Ces bourgeois, ils savent plus quoi inventer ! A force de lire et d''écrire, ils deviennent tous plus cons les uns qu''les autres.', 188, 39);
INSERT INTO QUOTES (quotes_text, quotes_refepisode, quotes_refcharacter) VALUES ('(À Roparzh) Excusez ! Y a moyen de vous entretenir deux secondes ? [Roparzh : Oui, c''est au sujet de quoi-t''est-ce ?] C''est au sujet que ce matin je suis passé par hasard devant vos enclos, et j''ai vu que vous aviez une jolie p''tite poule blanche ! [Roparzh : Oui, tout à fait !] Une jolie petite poule ''voyez, le bel animal […] C''est au sujet qu''en fait c''est la mienne, et que vous allez prendre un pain dans la tête, mais quelque chose de violent ! ', 189, 39);
INSERT INTO QUOTES (quotes_text, quotes_refepisode, quotes_refcharacter) VALUES ('(À Roparzh) Vous avez tué ma poule ?! Non mais est-ce que vous êtes pas un peu marteau, vous ? Parce que moi ça y est, j''ai les nerfs qui commencent à vriller ! Je vous montre : moi je bondis comme ça, et je vous arrive dessus en piqué diagonal. Et là c''est l''hymne à la cruauté, hein, un autel dressé au culte de la barbarie !', 189, 39);
INSERT INTO QUOTES (quotes_text, quotes_refepisode, quotes_refcharacter) VALUES ('(À Roparzh) Messieurs dames, dans quelques instants le grand spectacle de la souffrance. Si vous pouvez tâcher moyen de vous éloigner de 25 pieds, bons pieds, hein parce que ça va gicler un peu. !', 189, 39);
INSERT INTO QUOTES (quotes_text, quotes_refepisode, quotes_refcharacter) VALUES ('Ca fait deux cent ans qu''il y a que les paysans qui payent les taxes, alors je vois pas pourquoi aujourd''hui ça change, comme ça d''un seul coup et ça me tombe dessus comme une merde sur une planche !', 190, 40);
INSERT INTO QUOTES (quotes_text, quotes_refepisode, quotes_refcharacter) VALUES ('2500 pièces d''or ???! Eh... eh... C''est une blague? 2500 pièces d''or, mais où voulez vous que je trouve 2500 pièces d''or, dans le cul d''une vache ?!', 190, 40);
INSERT INTO QUOTES (quotes_text, quotes_refepisode, quotes_refcharacter) VALUES ('Excusez moi Sire, c''est sur le coup du...[dit à plusieurs reprises]', 190, 40);
INSERT INTO QUOTES (quotes_text, quotes_refepisode, quotes_refcharacter) VALUES ('Et le seigneur Lothar, il paye pas, lui, je sais il me l''a dit ...[Léodagan : non il paye pas, parce qu''il participe au combat]... Mais vous vous foutez de moi?! Il a au moins 95 ans, il a une jambe raide, il est tellement bigleux qu''il trouverait même pas sa bite pour pisser !', 190, 40);
INSERT INTO QUOTES (quotes_text, quotes_refepisode, quotes_refcharacter) VALUES ('Attendez, ça fait combien de dizaines d''années que je leur laisse cultiver mes terres, à ces connards ? En plus de ça ils sont même pas polis, ils sont cons... Comme des tables ! Ils font un pain dégueulasse - on s''pète les dents dessus - non, là, j''ai besoin d''un peu de pognon, alors j''aime autant vous dire qu''ils vont passer à la caisse, les pécores !', 190, 40);
INSERT INTO QUOTES (quotes_text, quotes_refepisode, quotes_refcharacter) VALUES ('Pour savoir s''il va y avoir du vent, il faut mettre son doigt dans le cul du coq.', 191, 41);
