INSERT INTO QUOTES (quotes_text, quotes_refepisode, quotes_refcharacter) VALUES ('Sire, sachez déjà que je ne suis pas homme à me méprendre sur le gaillard d''en face, mon petit doigt me dit que vous en êtes pas la moitié d''un !', 355, 53);
INSERT INTO QUOTES (quotes_text, quotes_refepisode, quotes_refcharacter) VALUES ('Au même titre que le bar est fermé, sauf si c''est un poisson, tout travail mérite salaire !', 355, 53);
INSERT INTO QUOTES (quotes_text, quotes_refepisode, quotes_refcharacter) VALUES ('Prêtez l''oreille sans ristourne !', 355, 53);
INSERT INTO QUOTES (quotes_text, quotes_refepisode, quotes_refcharacter) VALUES ('Tête bien pensante est par deux fois force de décision !', 355, 53);
